svg.script:
    var r = document.getElementById('r'),
        p = document.getElementById('p'),
        signaturePath = '',
        isDown = false;

    function clearSignature() {
      signaturePath = '';
      p.setAttribute('d', '');
    }

    function getSignature() {
      return signaturePath;
    }

	function setSignature(s) {
		signaturePath = s;
		p.setAttribute('d', signaturePath);
	}
